ucloud:udisk/diskAttachment:DiskAttachme.description:
Provides a Cloud Disk Attachment resource for attaching Cloud Disk to UHost Instance.

{{% examples %}}
## Example Usage
{{% example %}}

```typescript
import * as pulumi from "@pulumi/pulumi";
import * as ucloud from "@pulumi/ucloud";

const defaultZone = ucloud.uaccount.getZone({});
const defaultImage = defaultZone.then(defaultZone => ucloud.uhost.getImage({
    availabilityZone: defaultZone.zones[0].id,
    nameRegex: "^CentOS 7.[1-2] 64",
    imageType: "base",
}));
// Create cloud disk
const defaultDisk = new ucloud.udisk.Disk("defaultDisk", {
    availabilityZone: defaultZone.then(defaultZone => defaultZone.zones[0].id),
    diskSize: 10,
});
// Create a web server
const web = new ucloud.uhost.Instance("web", {
    availabilityZone: defaultZone.then(defaultZone => defaultZone.zones[0].id),
    instanceType: "n-basic-2",
    imageId: defaultImage.then(defaultImage => defaultImage.images[0].id),
    rootPassword: "wA1234567",
    tag: "tf-example",
});
// attach cloud disk to instance
const defaultDiskAttachment = new ucloud.udisk.DiskAttachment("defaultDiskAttachment", {
    availabilityZone: defaultZone.then(defaultZone => defaultZone.zones[0].id),
    diskId: defaultDisk.id,
    instanceId: web.id,
});
```
```python
import pulumi
import pulumi_ucloud as ucloud

default_zone = ucloud.uaccount.get_zone()
default_image = ucloud.uhost.get_image(availability_zone=default_zone.zones[0].id,
    name_regex="^CentOS 7.[1-2] 64",
    image_type="base")
# Create cloud disk
default_disk = ucloud.udisk.Disk("defaultDisk",
    availability_zone=default_zone.zones[0].id,
    disk_size=10)
# Create a web server
web = ucloud.uhost.Instance("web",
    availability_zone=default_zone.zones[0].id,
    instance_type="n-basic-2",
    image_id=default_image.images[0].id,
    root_password="wA1234567",
    tag="tf-example")
# attach cloud disk to instance
default_disk_attachment = ucloud.udisk.DiskAttachment("defaultDiskAttachment",
    availability_zone=default_zone.zones[0].id,
    disk_id=default_disk.id,
    instance_id=web.id)
```
```csharp
using Pulumi;
using Ucloud = Pulumi.Ucloud;

class MyStack : Stack
{
    public MyStack()
    {
        var defaultZone = Output.Create(Ucloud.Uaccount.GetZone.InvokeAsync());
        var defaultImage = defaultZone.Apply(defaultZone => Output.Create(Ucloud.Uhost.GetImage.InvokeAsync(new Ucloud.Uhost.GetImageArgs
        {
            AvailabilityZone = defaultZone.Zones[0].Id,
            NameRegex = "^CentOS 7.[1-2] 64",
            ImageType = "base",
        })));
        // Create cloud disk
        var defaultDisk = new Ucloud.Udisk.Disk("defaultDisk", new Ucloud.Udisk.DiskArgs
        {
            AvailabilityZone = defaultZone.Apply(defaultZone => defaultZone.Zones[0].Id),
            DiskSize = 10,
        });
        // Create a web server
        var web = new Ucloud.Uhost.Instance("web", new Ucloud.Uhost.InstanceArgs
        {
            AvailabilityZone = defaultZone.Apply(defaultZone => defaultZone.Zones[0].Id),
            InstanceType = "n-basic-2",
            ImageId = defaultImage.Apply(defaultImage => defaultImage.Images[0].Id),
            RootPassword = "wA1234567",
            Tag = "tf-example",
        });
        // attach cloud disk to instance
        var defaultDiskAttachment = new Ucloud.Udisk.DiskAttachment("defaultDiskAttachment", new Ucloud.Udisk.DiskAttachmentArgs
        {
            AvailabilityZone = defaultZone.Apply(defaultZone => defaultZone.Zones[0].Id),
            DiskId = defaultDisk.Id,
            InstanceId = web.Id,
        });
    }

}
```
```go
package main

import (
	"github.com/pulumi/pulumi-ucloud/sdk/go/ucloud/uaccount"
	"github.com/pulumi/pulumi-ucloud/sdk/go/ucloud/udisk"
	"github.com/pulumi/pulumi-ucloud/sdk/go/ucloud/uhost"
	"github.com/pulumi/pulumi/sdk/v3/go/pulumi"
)

func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		defaultZone, err := uaccount.GetZone(ctx, nil, nil)
		if err != nil {
			return err
		}
		opt0 := defaultZone.Zones[0].Id
		opt1 := "^CentOS 7.[1-2] 64"
		opt2 := "base"
		defaultImage, err := uhost.GetImage(ctx, &uhost.GetImageArgs{
			AvailabilityZone: &opt0,
			NameRegex:        &opt1,
			ImageType:        &opt2,
		}, nil)
		if err != nil {
			return err
		}
		defaultDisk, err := udisk.NewDisk(ctx, "defaultDisk", &udisk.DiskArgs{
			AvailabilityZone: pulumi.String(defaultZone.Zones[0].Id),
			DiskSize:         pulumi.Int(10),
		})
		if err != nil {
			return err
		}
		web, err := uhost.NewInstance(ctx, "web", &uhost.InstanceArgs{
			AvailabilityZone: pulumi.String(defaultZone.Zones[0].Id),
			InstanceType:     pulumi.String("n-basic-2"),
			ImageId:          pulumi.String(defaultImage.Images[0].Id),
			RootPassword:     pulumi.String("wA1234567"),
			Tag:              pulumi.String("tf-example"),
		})
		if err != nil {
			return err
		}
		_, err = udisk.NewDiskAttachment(ctx, "defaultDiskAttachment", &udisk.DiskAttachmentArgs{
			AvailabilityZone: pulumi.String(defaultZone.Zones[0].Id),
			DiskId:           defaultDisk.ID(),
			InstanceId:       web.ID(),
		})
		if err != nil {
			return err
		}
		return nil
	})
}
